vowel=0

i=0

array= text.split()

while (i < len(array)):
    word = array[i]
    j=0
    while (j < len(word)):
        
        if (word[j] == "a") or (word[j] == "A"):
            vowel= vowel+1
        elif (word[j] == "e") or (word[j] == "E"):
            vowel= vowel+1
        elif (word[j] == "i") or (word[j] == "I"):
            vowel= vowel+1
        elif (word[j] == "o") or (word[j] == "O"):
            vowel= vowel+1
        elif (word[j] == "u") or (word[j] == "U"):
            vowel= vowel+1
        j+=1
    i+=1

